import React, { useState, useEffect, useMemo, useCallback } from 'react';
import styled from '@emotion/styled';
import { motion } from 'framer-motion';
import confetti from 'canvas-confetti';
import { generateWordSet, evaluateGuess } from './gameLogic';

import gameplaySound from './gameplay.mp3';
import gameoverSound from './gameover.mp3';
import gamewinSound from './gamewin.mp3';

const WORD_LENGTH = 5;
const MAX_GUESSES = 6;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  font-family: 'Arial', sans-serif;
  background-color: #121213;
  color: white;
  min-height: 100vh;
  padding: 20px;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 1rem;
`;

const Grid = styled.div`
  display: grid;
  grid-template-rows: repeat(${MAX_GUESSES}, 1fr);
  gap: 5px;
  margin-bottom: 1rem;
`;

const Row = styled.div`
  display: grid;
  grid-template-columns: repeat(${WORD_LENGTH}, 1fr);
  gap: 5px;
`;

const Cell = styled(motion.div)`
  width: 60px;
  height: 60px;
  border: 2px solid #3a3a3c;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 2rem;
  font-weight: bold;
  text-transform: uppercase;
`;

const keyboardColors = [
  '#FF0000', '#FF3300', '#FF6600', '#FF9900', '#FFCC00', 
  '#FFFF00', '#CCFF00', '#99FF00', '#66FF00', '#33FF00'
];

const Keyboard = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  background-color: black;
  padding: 15px;
  border-radius: 10px;
  margin-top: 20px;
  width: 90%;
  max-width: 1000px;
`;

const KeyboardRow = styled.div`
  display: flex;
  justify-content: center;
  gap: 8px;
`;

const Key = styled(motion.button)`
  padding: 15px;
  font-size: 1.2rem;
  font-weight: bold;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  background-color: ${props => props.color};
  color: black;
  min-width: 40px;
  flex: ${props => props.flex || 1};

  &:hover {
    filter: brightness(90%);
  }
`;

const KeyboardTitle = styled.h2`
  font-size: 1.5rem;
  text-align: center;
  margin: 15px 0;
`;

const Wordle = () => {
  const [wordSet, setWordSet] = useState(new Set());
  const [correctWord, setCorrectWord] = useState('');
  const [guesses, setGuesses] = useState(Array(MAX_GUESSES).fill(''));
  const [currentGuess, setCurrentGuess] = useState('');
  const [gameOver, setGameOver] = useState(false);
  const [loading, setLoading] = useState(true);

  const [gameplayAudio] = useState(new Audio(gameplaySound));
  const [gameoverAudio] = useState(new Audio(gameoverSound));
  const [gamewinAudio] = useState(new Audio(gamewinSound));

  useEffect(() => {
    const fetchWord = async () => {
      setLoading(true);
      const { wordSet, todaysWord } = await generateWordSet();
      setWordSet(wordSet);
      setCorrectWord(todaysWord);
      setLoading(false);
    };

    fetchWord();
  }, []);

  const handleKeyPress = useCallback((key) => {
    if (gameOver || loading) return;

    if (key === 'ENTER' || key === 'Enter') {
      if (currentGuess.length !== WORD_LENGTH) return;
      if (!wordSet.has(currentGuess)) {
        alert('Not in word list');
        return;
      }

      const newGuesses = [...guesses];
      newGuesses[guesses.findIndex(guess => guess === '')] = currentGuess;
      setGuesses(newGuesses);
      setCurrentGuess('');

      if (currentGuess === correctWord) {
        setGameOver(true);
        triggerConfetti(); // Trigger confetti animation
        playGamewinSound(); // Play win sound effect
        alert('You won!');
      } else if (newGuesses[MAX_GUESSES - 1] !== '') {
        setGameOver(true);
        playGameoverSound(); // Play game over sound effect
        alert(`Game over! The word was ${correctWord}`);
      } else {
        playGameplaySound(); // Play gameplay sound effect
      }
    } else if (key === 'BACK' || key === 'Backspace') {
      setCurrentGuess(prev => prev.slice(0, -1));
    } else if (currentGuess.length < WORD_LENGTH && key.length === 1 && key.match(/[a-z]/i)) {
      setCurrentGuess(prev => prev + key.toUpperCase());
    }
  }, [currentGuess, guesses, wordSet, correctWord, gameOver, loading]);

  useEffect(() => {
    const handleKeyDown = (event) => {
      handleKeyPress(event.key); // This references the handleKeyPress function
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, []); // Remove handleKeyPress from the dependency array

  const keyboardLayout = [
    ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'],
    ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'],
    ['ENTER', 'Z', 'X', 'C', 'V', 'B', 'N', 'M', 'BACK']
  ];

  const keyColors = useMemo(() => {
    const colors = {};
    keyboardLayout.flat().forEach((key, index) => {
      colors[key] = keyboardColors[index % keyboardColors.length];
    });
    return colors;
  }, []);

  const triggerConfetti = () => {
    const end = Date.now() + 3 * 1000; // 3 seconds
    const colors = ["#a786ff", "#fd8bbc", "#eca184", "#f8deb1"];

    const frame = () => {
      if (Date.now() > end) return;

      confetti({
        particleCount: 2,
        angle: 60,
        spread: 55,
        startVelocity: 60,
        origin: { x: 0, y: 0.5 },
        colors: colors,
      });
      confetti({
        particleCount: 2,
        angle: 120,
        spread: 55,
        startVelocity: 60,
        origin: { x: 1, y: 0.5 },
        colors: colors,
      });

      requestAnimationFrame(frame);
    };

    frame();
  };

  const playGameplaySound = () => {
    gameplayAudio.currentTime = 0;
    gameplayAudio.play();
  };

  const playGameoverSound = () => {
    gameoverAudio.currentTime = 0;
    gameoverAudio.play();
  };

  const playGamewinSound = () => {
    gamewinAudio.currentTime = 0;
    gamewinAudio.play();
  };

  if (loading) {
    return <Container><Title>Loading...</Title></Container>;
  }

  return (
    <Container>
      <Title>Wordle Clone</Title>
      <Grid>
        {guesses.map((guess, i) => (
          <Row key={i}>
            {Array.from({ length: WORD_LENGTH }).map((_, j) => (
              <Cell
                key={j}
                initial={{ scale: 1 }}
                animate={{
                  scale: [1, 1.1, 1],
                  rotate: [0, 10, -10, 0],
                  backgroundColor: guess
                    ? evaluateGuess(guess, correctWord)[j] === 'correct'
                      ? '#6aaa64'
                      : evaluateGuess(guess, correctWord)[j] === 'present'
                      ? '#c9b458'
                      : '#3a3a3c'
                    : 'transparent',
                  color: guess ? '#fff' : '#fff',
                }}
                transition={{ duration: 0.3 }}
              >
                {guess[j] || (i === guesses.findIndex(g => g === '') ? currentGuess[j] : '')}
              </Cell>
            ))}
          </Row>
        ))}
      </Grid>
      <KeyboardTitle>EYES ON THE SCREEN</KeyboardTitle>
      <Keyboard>
        {keyboardLayout.map((row, rowIndex) => (
          <KeyboardRow key={rowIndex}>
            {row.map((key) => (
              <Key
                key={key}
                onClick={() => handleKeyPress(key)}
                whileTap={{ scale: 0.9 }}
                color={keyColors[key]}
                flex={key === 'ENTER' || key === 'BACK' ? 2 : 1}
              >
                {key}
              </Key>
            ))}
          </KeyboardRow>
        ))}
      </Keyboard>
    </Container>
  );
};

export default Word
