// Wordle.js

import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { motion } from 'framer-motion';
import confetti from 'canvas-confetti';
import { generateWordSet, evaluateGuess } from './gameLogic';
import './Wordle.css';

import gameplaySound from './assets/gameplay.mp3';
import gameoverSound from './assets/gameover.mp3';
import gamewinSound from './assets/gamewin.mp3';

const WORD_LENGTH = 5;
const MAX_GUESSES = 6;

const keyboardColors = [
  '#FF0000', '#FF3300', '#FF6600', '#FF9900', '#FFCC00', 
  '#FFFF00', '#CCFF00', '#99FF00', '#66FF00', '#33FF00'
];

const Wordle = () => {
  const [wordSet, setWordSet] = useState(new Set());
  const [correctWord, setCorrectWord] = useState('');
  const [guesses, setGuesses] = useState(Array(MAX_GUESSES).fill(''));
  const [currentGuess, setCurrentGuess] = useState('');
  const [gameOver, setGameOver] = useState(false);
  const [loading, setLoading] = useState(true);

  const [gameplayAudio] = useState(() => {
    const audio = new Audio(gameplaySound);
    audio.loop = true;
    return audio;
  });
  const [gameoverAudio] = useState(new Audio(gameoverSound));
  const [gamewinAudio] = useState(new Audio(gamewinSound));

  useEffect(() => {
    return () => {
      gameplayAudio.pause();
    };
  }, [gameplayAudio]);

  useEffect(() => {
    const fetchWord = async () => {
      setLoading(true);
      const { wordSet, todaysWord } = await generateWordSet();
      setWordSet(wordSet);
      setCorrectWord(todaysWord);
      setLoading(false);
    };

    fetchWord();
  }, []);

  const handleKeyPress = useCallback((key) => {
    if (gameOver || loading) return;

    if (!gameplayAudio.paused) {
      gameplayAudio.play().catch(error => console.error('Playback failed:', error));
    }

    if (key === 'ENTER' || key === 'Enter') {
      if (currentGuess.length !== WORD_LENGTH) return;
      if (!wordSet.has(currentGuess)) {
        alert('Not in word list');
        return;
      }

      const newGuesses = [...guesses];
      newGuesses[guesses.findIndex(guess => guess === '')] = currentGuess;
      setGuesses(newGuesses);
      setCurrentGuess('');

      if (currentGuess === correctWord) {
        setGameOver(true);
        triggerConfetti();
        playGamewinSound();
        alert('You won!');
      } else if (newGuesses[MAX_GUESSES - 1] !== '') {
        setGameOver(true);
        playGameoverSound();
        alert(`Game over! The word was ${correctWord}`);
      }
    } else if (key === 'BACK' || key === 'Backspace') {
      setCurrentGuess(prev => prev.slice(0, -1));
    } else if (currentGuess.length < WORD_LENGTH && key.length === 1 && key.match(/[a-z]/i)) {
      setCurrentGuess(prev => prev + key.toUpperCase());
    }
  }, [currentGuess, guesses, wordSet, correctWord, gameOver, loading, gameplayAudio]);

  useEffect(() => {
    const handleKeyDown = (event) => {
      handleKeyPress(event.key);
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [handleKeyPress]);

  const keyboardLayout = [
    ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'],
    ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'],
    ['ENTER', 'Z', 'X', 'C', 'V', 'B', 'N', 'M', 'BACK']
  ];

  const keyColors = useMemo(() => {
    const colors = {};
    keyboardLayout.flat().forEach((key, index) => {
      colors[key] = keyboardColors[index % keyboardColors.length];
    });
    return colors;
  }, []);

  const triggerConfetti = () => {
    const end = Date.now() + 3 * 1000;
    const colors = ["#a786ff", "#fd8bbc", "#eca184", "#f8deb1"];

    const frame = () => {
      if (Date.now() > end) return;

      confetti({
        particleCount: 2,
        angle: 60,
        spread: 55,
        startVelocity: 60,
        origin: { x: 0, y: 0.5 },
        colors: colors,
      });
      confetti({
        particleCount: 2,
        angle: 120,
        spread: 55,
        startVelocity: 60,
        origin: { x: 1, y: 0.5 },
        colors: colors,
      });

      requestAnimationFrame(frame);
    };

    frame();
  };

  const playGameoverSound = () => {
    gameoverAudio.currentTime = 0;
    gameoverAudio.play().catch(error => console.error('Playback failed:', error));
  };

  const playGamewinSound = () => {
    gamewinAudio.currentTime = 0;
    gamewinAudio.play().catch(error => console.error('Playback failed:', error));
  };

  if (loading) {
    return <div className="container"><h1 className="title">Loading...</h1></div>;
  }

  return (
    <div className="container">
      <h1 className="title">Wordle Clone</h1>
      <div className="grid">
        {guesses.map((guess, i) => (
          <div className="row" key={i}>
            {Array.from({ length: WORD_LENGTH }).map((_, j) => (
              <motion.div
                className="cell"
                key={j}
                initial={{ backgroundColor: '#121213' }}
                animate={{
                  scale: [1, 1.1, 1],
                  rotate: [0, 10, -10, 0],
                  backgroundColor: guess[j] ? '#6aaa64' : '#121213',
                  color: '#fff',
                }}
                transition={{ duration: 0.3 }}
              >
                {guess[j] || ''}
              </motion.div>
            ))}
          </div>
        ))}
      </div>
      <div className="keyboard">
        <h2 className="keyboard-title">Keyboard</h2>
        {keyboardLayout.map((row, rowIndex) => (
          <div className="keyboard-row" key={rowIndex}>
            {row.map((key, keyIndex) => (
              <motion.button
                className="key"
                key={keyIndex}
                style={{ backgroundColor: keyColors[key] }}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                onClick={() => handleKeyPress(key)}
              >
                {key}
              </motion.button>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Wordle;
