// src/Wordle.js

import React, { useState, useEffect } from 'react';
import styled from '@emotion/styled';
import { motion } from 'framer-motion';
import { generateWordSet, evaluateGuess } from './gameLogic';

const WORD_LENGTH = 5;
const MAX_GUESSES = 6;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
  background-color: #121213;
  min-height: 100vh;
  color: #ffffff;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  margin: 1rem 0;
`;

const Grid = styled.div`
  display: grid;
  grid-template-rows: repeat(${MAX_GUESSES}, 1fr);
  gap: 5px;
  margin-bottom: 1rem;
`;

const Row = styled.div`
  display: grid;
  grid-template-columns: repeat(${WORD_LENGTH}, 1fr);
  gap: 5px;
`;

const Cell = styled(motion.div)`
  width: 50px;
  height: 50px;
  border: 2px solid #3a3a3c;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 1.5rem;
  font-weight: bold;
  text-transform: uppercase;
`;

const Keyboard = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 1rem;
`;

const KeyboardRow = styled.ul`
  list-style: none;
  display: flex;
  margin: 0;
  padding: 0;
`;

const Key = styled(motion.li)`
  height: 3em;
  min-width: 2em;
  color: #ffffff;
  border-radius: 0.4em;
  line-height: 3em;
  letter-spacing: 1px;
  margin: 0.4em;
  transition: 0.3s;
  text-align: center;
  font-size: 1em;
  background-color: #818384;
  cursor: pointer;
  user-select: none;

  &.wide {
    min-width: 4em;
  }

  &.correct {
    background-color: #538d4e;
  }

  &.present {
    background-color: #b59f3b;
  }

  &.absent {
    background-color: #3a3a3c;
  }
`;

const Wordle = () => {
  const [wordSet, setWordSet] = useState(new Set());
  const [correctWord, setCorrectWord] = useState('');
  const [guesses, setGuesses] = useState(Array(MAX_GUESSES).fill(''));
  const [currentGuess, setCurrentGuess] = useState('');
  const [gameOver, setGameOver] = useState(false);
  const [keyStatus, setKeyStatus] = useState({});

  useEffect(() => {
    generateWordSet().then(({ wordSet, todaysWord }) => {
      setWordSet(wordSet);
      setCorrectWord(todaysWord.toUpperCase());
    });
  }, []);

  const handleKeyPress = (key) => {
    if (gameOver) return;

    if (key === 'ENTER') {
      if (currentGuess.length !== WORD_LENGTH) return;
      if (!wordSet.has(currentGuess.toLowerCase())) {
        alert('Not in word list');
        return;
      }

      const newGuesses = [...guesses];
      const currentGuessIndex = guesses.findIndex(guess => guess === '');
      newGuesses[currentGuessIndex] = currentGuess;
      setGuesses(newGuesses);

      const evaluation = evaluateGuess(currentGuess, correctWord);
      const newKeyStatus = { ...keyStatus };
      evaluation.forEach((status, index) => {
        const letter = currentGuess[index];
        if (!newKeyStatus[letter] || status === 'correct') {
          newKeyStatus[letter] = status;
        }
      });
      setKeyStatus(newKeyStatus);

      setCurrentGuess('');

      if (currentGuess === correctWord) {
        setGameOver(true);
        alert('You won!');
      } else if (currentGuessIndex === MAX_GUESSES - 1) {
        setGameOver(true);
        alert(`Game over! The word was ${correctWord}`);
      }
    } else if (key === 'BACK') {
      setCurrentGuess(currentGuess.slice(0, -1));
    } else if (currentGuess.length < WORD_LENGTH && key.length === 1) {
      setCurrentGuess(currentGuess + key);
    }
  };

  const keyboardLayout = [
    ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'],
    ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'],
    ['ENTER', 'Z', 'X', 'C', 'V', 'B', 'N', 'M', 'BACK'],
  ];

  return (
    <Container>
      <Title>Wordle Clone</Title>
      <Grid>
        {guesses.map((guess, i) => (
          <Row key={i}>
            {Array.from({ length: WORD_LENGTH }).map((_, j) => (
              <Cell
                key={j}
                initial={{ scale: 1 }}
                animate={{
                  scale: [1, 1.1, 1],
                  rotate: [0, 10, -10, 0],
                  backgroundColor: guess
                    ? evaluateGuess(guess, correctWord)[j] === 'correct'
                      ? '#538d4e'
                      : evaluateGuess(guess, correctWord)[j] === 'present'
                      ? '#b59f3b'
                      : '#3a3a3c'
                    : '#121213',
                  borderColor: guess ? '#3a3a3c' : '#3a3a3c',
                }}
                transition={{ duration: 0.3 }}
              >
                {guess[j] || (i === guesses.findIndex(g => g === '') ? currentGuess[j] : '')}
              </Cell>
            ))}
          </Row>
        ))}
      </Grid>
      <Keyboard>
        {keyboardLayout.map((row, i) => (
          <KeyboardRow key={i}>
            {row.map((key) => (
              <Key
                key={key}
                className={`${key.length > 1 ? 'wide' : ''} ${keyStatus[key] || ''}`}
                onClick={() => handleKeyPress(key)}
                whileTap={{ scale: 0.95 }}
              >
                {key === 'BACK' ? '⌫' : key}
              </Key>
            ))}
          </KeyboardRow>
        ))}
      </Keyboard>
    </Container>
  );
};

export default Wordle;
