// gameLogic.jsx

const WORD_LIST = [
  'abide', 'abuse', 'actor', 'acute', 'admit', 'adopt', 'adult', 'after', 'again', 'agent',
  'agree', 'ahead', 'alarm', 'album', 'alert', 'alike', 'alive', 'allow', 'alone', 'along',
  'alter', 'among', 'anger', 'angle', 'angry', 'apart', 'apple', 'apply', 'argue', 'arise',
  'armed', 'array', 'aside', 'asset', 'audio', 'audit', 'avoid', 'award', 'aware', 'badly',
  'baker', 'bases', 'basic', 'basis', 'beach', 'began', 'begin', 'begun', 'being', 'below',
  'bench', 'billy', 'birth', 'black', 'blame', 'blind', 'block', 'blood', 'board', 'boost',
  'booth', 'bound', 'brain', 'brand', 'bread', 'break', 'breed', 'brief', 'bring', 'broad',
  'broke', 'brown', 'build', 'built', 'buyer', 'cable', 'calif', 'carry', 'catch', 'cause',
  'chain', 'chair', 'chart', 'chase', 'cheap', 'check', 'chest', 'chief', 'child', 'china'
];

export const generateWordSet = async () => {
  const today = new Date();
  const wordSet = new Set();

  // Select a random word from WORD_LIST
  const index = today.getDate() % WORD_LIST.length;
  const todaysWord = WORD_LIST[index];

  // Generate a set of all characters in the selected word
  for (let char of todaysWord) {
    wordSet.add(char);
  }

  return { wordSet, todaysWord };
};

export const evaluateGuess = (guess, correctWord) => {
  const result = [];
  const guessedSet = new Set(guess);
  const correctSet = new Set(correctWord);

  for (let i = 0; i < correctWord.length; i++) {
    if (guess[i] === correctWord[i]) {
      result.push('correct');
    } else if (correctWord.includes(guess[i])) {
      result.push('present');
    } else {
      result.push('absent');
    }
  }

  return result;
};
