// src/Wordle.js

import React, { useState, useEffect, useMemo, useCallback } from 'react';
import styled from '@emotion/styled';
import { motion } from 'framer-motion';
import { generateWordSet, evaluateGuess } from './gameLogic';

const WORD_LENGTH = 5;
const MAX_GUESSES = 6;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  font-family: 'Arial', sans-serif;
  background-color: #121213;
  color: white;
  min-height: 100vh;
  padding: 20px;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 1rem;
`;

const Grid = styled.div`
  display: grid;
  grid-template-rows: repeat(${MAX_GUESSES}, 1fr);
  gap: 5px;
  margin-bottom: 1rem;
`;

const Row = styled.div`
  display: grid;
  grid-template-columns: repeat(${WORD_LENGTH}, 1fr);
  gap: 5px;
`;

const Cell = styled(motion.div)`
  width: 60px;
  height: 60px;
  border: 2px solid #3a3a3c;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 2rem;
  font-weight: bold;
  text-transform: uppercase;
`;

const keyboardColors = [
  '#FF0000', '#FF3300', '#FF6600', '#FF9900', '#FFCC00', 
  '#FFFF00', '#CCFF00', '#99FF00', '#66FF00', '#33FF00'
];

const Keyboard = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  background-color: black;
  padding: 15px;
  border-radius: 10px;
  margin-top: 20px;
  width: 90%;
  max-width: 1000px;
`;

const KeyboardRow = styled.div`
  display: flex;
  justify-content: center;
  gap: 8px;
`;

const Key = styled(motion.button)`
  padding: 15px;
  font-size: 1.2rem;
  font-weight: bold;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  background-color: ${props => props.color};
  color: black;
  min-width: 40px;
  flex: ${props => props.flex || 1};

  &:hover {
    filter: brightness(90%);
  }
`;

const KeyboardTitle = styled.h2`
  font-size: 1.5rem;
  text-align: center;
  margin: 15px 0;
`;

const Message = styled.div`
  background-color: #ff0000;
  color: #ffffff;
  padding: 10px;
  border-radius: 5px;
  margin-top: 10px;
  text-align: center;
`;

const Wordle = () => {
  const [wordSet, setWordSet] = useState(new Set());
  const [correctWord, setCorrectWord] = useState('');
  const [guesses, setGuesses] = useState(Array(MAX_GUESSES).fill(''));
  const [currentGuess, setCurrentGuess] = useState('');
  const [gameOver, setGameOver] = useState(false);
  const [loading, setLoading] = useState(true);
  const [invalidWord, setInvalidWord] = useState(false);

  useEffect(() => {
    const fetchWord = async () => {
      setLoading(true);
      const { wordSet, todaysWord } = await generateWordSet();
      setWordSet(wordSet);
      setCorrectWord(todaysWord);
      setLoading(false);
    };

    fetchWord();
  }, []);

  const handleKeyPress = useCallback((key) => {
    if (gameOver || loading) return;

    if (key === 'ENTER' || key === 'Enter') {
      if (currentGuess.length !== WORD_LENGTH) return;
      if (!wordSet.has(currentGuess)) {
        setInvalidWord(true);
        setTimeout(() => setInvalidWord(false), 2000);
        return;
      }

      const newGuesses = [...guesses];
      newGuesses[guesses.findIndex(guess => guess === '')] = currentGuess;
      setGuesses(newGuesses);
      setCurrentGuess('');

      if (currentGuess === correctWord) {
        setGameOver(true);
        alert('You won!');
      } else if (newGuesses[MAX_GUESSES - 1] !== '') {
        setGameOver(true);
        alert(`Game over! The word was ${correctWord}`);
      }
    } else if (key === 'BACK' || key === 'Backspace') {
      setCurrentGuess(prev => prev.slice(0, -1));
    } else if (currentGuess.length < WORD_LENGTH && key.length === 1 && key.match(/[a-z]/i)) {
      setCurrentGuess(prev => prev + key.toUpperCase());
    }
  }, [currentGuess, guesses, wordSet, correctWord, gameOver, loading]);

  useEffect(() => {
    const handleKeyDown = (event) => {
      handleKeyPress(event.key);
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [handleKeyPress]);

  const keyboardLayout = [
    ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'],
    ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'],
    ['ENTER', 'Z', 'X', 'C', 'V', 'B', 'N', 'M', 'BACK']
  ];

  const keyColors = useMemo(() => {
    const colors = {};
    keyboardLayout.flat().forEach((key, index) => {
      colors[key] = keyboardColors[index % keyboardColors.length];
    });
    return colors;
  }, []);

  const getCellColor = (guess, index) => {
    const evaluations = evaluateGuess(guess, correctWord);
    return evaluations[index] === 'correct' ? '#6aaa64'
         : evaluations[index] === 'present' ? '#c9b458'
         : '#3a3a3c';
  };

  if (loading) {
    return <Container><Title>Loading...</Title></Container>;
  }

  return (
    <Container>
      <Title>Wordle Clone</Title>
      <Grid>
        {guesses.map((guess, i) => (
          <Row key={i}>
            {Array.from({ length: WORD_LENGTH }).map((_, j) => (
              <Cell
                key={j}
                initial={{ scale: 1 }}
                animate={{
                  scale: [1, 1.1, 1],
                  rotate: [0, 10, -10, 0],
                  backgroundColor: guess
                    ? getCellColor(guess, j)
                    : 'transparent',
                  color: guess ? '#fff' : '#fff',
                }}
                transition={{ duration: 0.3 }}
              >
                {guess[j] || (i === guesses.findIndex(g => g === '') ? currentGuess[j] : '')}
              </Cell>
            ))}
          </Row>
        ))}
      </Grid>
      {invalidWord && <Message>Not in word list</Message>}
      <KeyboardTitle>EYES ON THE SCREEN</KeyboardTitle>
      <Keyboard>
        {keyboardLayout.map((row, rowIndex) => (
          <KeyboardRow key={rowIndex}>
            {row.map((key) => (
              <Key
                key={key}
                onClick={() => handleKeyPress(key)}
                whileTap={{ scale: 0.95 }}
                color={keyColors[key]}
                flex={key === 'BACK' || key === 'ENTER' ? 1.5 : 1}
              >
                {key === 'BACK' ? '⌫' : key}
              </Key>
            ))}
          </KeyboardRow>
        ))}
      </Keyboard>
      <KeyboardTitle>HANDS ON THE KEYBOARD</KeyboardTitle>
    </Container>
  );
};

export default Wordle;
